<?php

namespace AppBundle\Repository;

use Doctrine\ORM\QueryBuilder;

/**
 * HargaOTRRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class HargaOTRRepository extends \Doctrine\ORM\EntityRepository
{
    private $alias = 'hargaOTR';

    public function query(QueryBuilder $queryBuilder = null)
    {
        if (is_null($queryBuilder)) {
            $queryBuilder = $this->createQueryBuilder($this->alias);
        }

        return $queryBuilder;
    }

    public function queryById($id, QueryBuilder $queryBuilder = null)
    {
        if (is_null($queryBuilder)) {
            $queryBuilder = $this->createQueryBuilder($this->alias);
        }

        return $queryBuilder->andWhere($queryBuilder->expr()->eq($this->alias . '.id', ':id'))
            ->setParameter(':id', $id);
    }

    public function queryByKotaId($kotaId, QueryBuilder $queryBuilder = null)
    {
        if (is_null($queryBuilder)) {
            $queryBuilder = $this->createQueryBuilder($this->alias);
        }

        return $queryBuilder->andWhere($queryBuilder->expr()->eq($this->alias . '.kota', ':kotaId'))
            ->setParameter(':kotaId', $kotaId);
    }

    public function queryByKendaraanId($kendaraanId, QueryBuilder $queryBuilder = null)
    {
        if (is_null($queryBuilder)) {
            $queryBuilder = $this->createQueryBuilder($this->alias);
        }

        return $queryBuilder->andWhere($queryBuilder->expr()->eq($this->alias . '.kendaraan', ':kendaraanId'))
            ->setParameter(':kendaraanId', $kendaraanId);
    }

    public function queryByKotaIdAndKendaraanId($kotaId, $kendaraanId, QueryBuilder $queryBuilder = null)
    {
        if (is_null($queryBuilder)) {
            $queryBuilder = $this->createQueryBuilder($this->alias);
        }

        return $queryBuilder->andWhere($queryBuilder->expr()->eq($this->alias . '.kendaraan', ':kendaraanId'))
            ->andWhere($queryBuilder->expr()->eq($this->alias . '.kota', ':kotaId'))
            ->setParameter(':kendaraanId', $kendaraanId)
            ->setParameter(':kotaId', $kotaId);
    }
}
